namespace Cocktails.Api.Infrastructure.Resources.Test;

using Cocktails.Api.Domain.Services;
using System.IO;

public class CypressUser : IFileAccessor
{
    private static readonly string base64ProfileImage = "";

    private static readonly byte[] imageBytes = Convert.FromBase64String(base64ProfileImage);

    private static byte[] GetProfileImageBytes() => imageBytes;

    public string GetContentType() => "image/webp";

    public string GetFileName() => "cypress-user-profile-image.webp";

    public long GetLength() => GetProfileImageBytes().LongLength;

    public Stream GetStream()
    {
        var ms = new MemoryStream(imageBytes);
        ms.Seek(0, SeekOrigin.Begin);
        ms.Position = 0;
        return ms;
    }
}